mapper.select:
select no, region, kind, name, image, introduce, hit
from (
select rownum rnum, no, region, kind, name, image, introduce, hit
from (
select no, region, kind, name, image, introduce, hit
from diner 

order by no desc
)
) where rnum between #{startRow} and #{endRow}

sql.if:

where 1=0

