mapper.select:
select no, id, area, name, content, bus_tel, bus_mail, bus_address, fileName
from(
	select rownum rnum, no, id, area, name, content, bus_tel, bus_mail, bus_address, fileName
	from(
		select no, id, area, name, content, bus_tel, bus_mail, bus_address, fileName
		from business

		order by no desc
	)
) where rnum between #{startRow} and #{endRow}

select no, id, area, name, content, bus_tel, bus_mail, bus_address, fileName
from business 
where no = #{no}

mapper.update:
update business set id = #{id}, area = #{area}, name = #{name}, content = #{content}, bus_tel = #{bus_tel}, bus_mail = #{bus_mail},
bus_address = #{bus_address}, fileName = #{fileName}
where no = #{no}
