mapper.select:

		SELECT ro_no, no, ro_name, id, ro_price, ro_people, ro_info, ro_state, fileName 
		FROM (
		    SELECT rownum rnum, ro_no, no, ro_name, id, ro_price, ro_people, ro_info, ro_state, fileName 
		FROM (
		    	select r.ro_no, r.no, r.ro_name, r.id, r.ro_price, r.ro_people, r.ro_info, r.ro_state, r.fileName 
       			 from room r, business b
				 where r.no = b.no and b.no = #{no}

		        order by ro_no desc
		        )
		) where rnum between #{pageObject.startRow} and #{pageObject.endRow}


select count(*)
from  room r, business b
	 where r.no = b.no and b.no = #{no}

select ro_no, ro_name, id, ro_price, ro_people, ro_info, ro_state, fileName 
from room 
where ro_no = #{ro_no}
